        return new Source("{% extends 'base.html.twig' %}

{% block title %}Company index{% endblock %}

{% block body %}
    <h1>Liste des entreprises</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Nom</th>
                <th>Adresse</th>
                <th>Numéro de téléphone</th>
                <th>Actions</th>
            </tr>
        </thead>
        <tbody>
        {% for company in companies %}
            <tr>
                <td>{{ company.id }}</td>
                <td>{{ company.name }}</td>
                <td>{{ company.address }}</td>
                <td>{{ company.phone }}</td>
                <td>
                    <a href=\"{{ path('app_company_show', {'id': company.id}) }}\">Voir</a>
                    <a href=\"{{ path('app_company_edit', {'id': company.id}) }}\">Modifier</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <button type=\"button\" class=\"btn btn-outline-primary\"><a href=\"{{ path('app_company_new') }}\">Ajouter une entreprise</a></button>
{% endblock %}
", "company/index.html.twig", "/Users/celine/Desktop/DFS/Symfony/TP1/templates/company/index.html.twig");
